using NetCorePal.Extensions.Domain;using Xiangjiandao.Domain.Enums;namespace Xiangjiandao.Domain.AggregatesModel.AdminUserAggregate;/// <summary>/// 管理员 Id/// </summary>public partial record AdminUserId : IGuidStronglyTypedId;/// <summary>/// 管理员/// </summary>public class AdminUser : Entity<AdminUserId>, IAggregateRoot{    protected AdminUser()    {    }    /// <summary>    /// 注册邮箱    /// </summary>     public string Email { get; private set; } = string.Empty;        /// <summary>    /// 手机号    /// </summary>     public string Phone { get; private set; } = string.Empty;    /// <summary>    /// 手机区号    /// </summary>     public string PhoneRegion { get; private set; } = string.Empty;    /// <summary>    /// 用户头像    /// </summary>     public string Avatar { get; private set; } = string.Empty;    /// <summary>    /// 用户角色    /// </summary>     public RoleType Role { get; private set; } = RoleType.Unknown;        /// <summary>    /// 是否特殊账号超级管理员    /// </summary>    public bool Special { get; private set; } = false;    /// <summary>    /// 密码摘要    /// </summary>     public SecretData SecretData { get; private set; } = default!;    /// <summary>    /// 创建时间    /// </summary>     public DateTimeOffset CreatedAt { get; private set; } = DateTimeOffset.UtcNow;    /// <summary>    /// 创建者    /// </summary>     public string CreatedBy { get; private set; } = string.Empty;    /// <summary>    /// 更新时间    /// </summary>     public UpdateTime UpdatedAt { get; set; } = new UpdateTime(DateTimeOffset.MinValue);    /// <summary>    /// 更新者    /// </summary>     public string UpdatedBy { get; private set; } = string.Empty;    /// <summary>    /// 是否删除    /// </summary>     public Deleted Deleted { get; private set; } = new Deleted(false);    ///<summary>    /// 创建管理员    ///</summary>    public static AdminUser Create(        string email,        string avatar,        RoleType role,        SecretData secretData    )    {        var instance = new AdminUser        {            Email = email,            Avatar = avatar,            Role = role,            SecretData = secretData,            CreatedAt = DateTimeOffset.UtcNow,            CreatedBy = string.Empty,            Deleted = false,        };        return instance;    }    ///<summary>    /// 创建管理员    ///</summary>    public static AdminUser CreateAdmin(        string email,        string phone,        string phoneRegion,        string avatar,        SecretData secretData     )    {        var instance = new AdminUser        {            Email = email,            Phone = phone,            PhoneRegion = phoneRegion,            Avatar = avatar,            Role = RoleType.Admin,            Special = false,            SecretData = secretData,            CreatedAt = DateTimeOffset.UtcNow,        };                return instance;    }    ///<summary>    /// 创建运营人员    ///</summary>    public static AdminUser CreateOperator(        string email,        string phone,        string phoneRegion,        string avatar,        SecretData secretData    )    {        var instance = new AdminUser        {            Email = email,            Phone = phone,            PhoneRegion = phoneRegion,            Avatar = avatar,            Role = RoleType.Operator,            Special = false,            SecretData = secretData,            CreatedAt = DateTimeOffset.UtcNow,        };                return instance;    }    /// <summary>    /// 修改密码    /// </summary>    public void ModifyPassword(SecretData secretData)    {        SecretData = secretData;    }    /// <summary>    /// 修改用户头像    /// </summary>    /// <param name="avatar"></param>    /// <returns></returns>    public bool ModifyAvatar(string avatar)    {        Avatar = avatar;        return true;    }        /// <summary>    /// 软删除管理员    /// </summary>    public bool Delete(){        Deleted = true;        return true;    }}